import React from 'react';
import './AuthForm.css';
import { Link } from 'react-router-dom';

function AuthForm({formTitle, formButtonText, formContentText, formRedirectText, formPadding, children }) {
    return (
        <div className="auth-form">
            <Link to="/" className="auth-form__logo" />
            <h2 className="auth-form__title">{ formTitle }</h2>
            <fieldset className="auth-form__fieldset">
                {children}
                <label className="auth-form__submit" style={ { padding: formPadding } } htmlFor="register">
                    <button type="submit" name="register" className="auth-form__button">{ formButtonText }</button>
                </label>
            </fieldset>
            <div className="auth-form__redirect">
                <p className="auth-form__content">{ formContentText }&nbsp;</p>
                <Link to="/signin" className="auth-form__link">{ formRedirectText }</Link>
            </div>
        </div>
    )
}

export default AuthForm;
