import React from 'react';
import './MoviesCard.css';

function MoviesCard({card, page}) {

    let like;
    let remove;

const isLiked = () => {
    if (page) {
        like = `(movies-card__like-place ${card.like ? 'movies-card__like' : 'movies-card__unlike'})`;
        return like;
    }
    if (!page) {
        remove = `(movies-card__like-place ${card.like ? 'movies-card__remove' : ''})`;
        return remove;
    }    
}
console.log(isLiked);
    return (
        <div className="movies-card">
            <div className="movies-card__content">
                <div className="movies-card__about">
                    <h5 className="movies-card__title">{card.name}</h5>
                    <p className="movies-card__duration">{card.duration}</p>
                </div>
                <button type="button" className={isLiked} />
            </div>
            <div style ={ { backgroundImage : `url(${card.image})` } } className="movies-card__image"></div>
        </div>
    )
}

export default MoviesCard;
