import React, {useState, useEffect} from 'react';
import '../App/App.css';
import { Route, Switch } from 'react-router-dom';
import Header from '../Header/Header';
import Main from '../Main/Main';
import Movies from '../Movies/Movies';
import SavedMovies from '../SavedMovies/SavedMovies';
import Profile from '../Profile/Profile';
import Login from '../Login/Login';
import Register from '../Register/Register';
import Footer from '../Footer/Footer';

import cards from '../../utils/cards';
import savedCards from '../../utils/saved-cards';

function App() {

const [isFooterShow, setIsFooterShow] = useState('display: block');
const [isPathSelector, setIsPathSelector] = useState('/');

function footerHiding() {
  if (Location.pathname === '/profile') {
    setIsFooterShow('display: none')
  }
}

  return (
    <div className="App">

      <Header />

      <Switch>
        <Route exact path="/">
          <Main />
        </Route>        
        <Route path="/movies">
          <Movies cards={cards} />
        </Route>
        <Route path="/saved-movies">
          <SavedMovies cards={savedCards} />
        </Route>
        <Route path="/profile">
          <Profile />
        </Route>
        <Route path="/signin">
          <Login />
        </Route>
        <Route path="/signup">
          <Register />
        </Route>
      </Switch>

      <Footer />
    </div>
  );
}

export default App;
